import React, { useState, useEffect } from "react";
import axios from "axios";
import "./.css"; // Import CSS

const Home = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setSelectedFile(file);
    setPreview(URL.createObjectURL(file));
    setData(null);
  };

  useEffect(() => {
    const upload = async () => {
      if (!selectedFile) return;
      setLoading(true);
      const formData = new FormData();
      formData.append("file", selectedFile);

      try {
        const response = await axios.post(
          process.env.REACT_APP_API_URL,
          formData,
          {
            headers: { "Content-Type": "multipart/form-data" },
          }
        );
        if (response.status === 200) setData(response.data);
      } catch (err) {
        console.error("Upload error", err);
      } finally {
        setLoading(false);
      }
    };
    upload();
  }, [selectedFile]);

  const handleClear = () => {
    setSelectedFile(null);
    setPreview(null);
    setData(null);
  };

  return (
    <div className="container">
      <header className="header">Potato Leaf Disease Classifier</header>

      {!preview && (
        <label className="upload-box">
          <input type="file" onChange={handleFileChange} accept="image/*" />
          <p>Click or drag a potato leaf image here</p>
        </label>
      )}

      {preview && (
        <div className="preview-card">
          <img src={preview} alt="Uploaded" className="preview-img" />
          <div className="prediction">
            {loading ? (
              <p className="loading">Analyzing...</p>
            ) : data ? (
              <>
                <p><strong>Prediction:</strong> {data.class}</p>
                <p><strong>Confidence:</strong> {(parseFloat(data.confidence) * 100).toFixed(2)}%</p>
              </>
            ) : (
              <p>Waiting for prediction...</p>
            )}
          </div>
          <button className="clear-btn" onClick={handleClear}>Clear</button>
        </div>
      )}
    </div>
  );
};

export default Home;
